package ru.jmentor.SpringBootExample.controllers;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import ru.jmentor.SpringBootExample.model.User;import ru.jmentor.SpringBootExample.model.UserForm;import ru.jmentor.SpringBootExample.service.UserService;@RestController@RequestMapping("/rest")public class AdminRestController {    private UserService service;    @Autowired    public void setService(UserService service) { this.service = service; }    @GetMapping    public ResponseEntity<?> usersTable() {        return ResponseEntity.ok(service.getAllUsers());    }    @PostMapping    public void adminCreateUser(@RequestBody UserForm userForm) {        User userById = new User();        userById.setUserName(userForm.getUserName());        userById.setUserPassword(userForm.getUserPassword());        userById.setUserEmail(userForm.getUserEmail());        service.saveUser(userById);    }    @DeleteMapping    public void adminDeleteUser(@RequestBody Long id) {        service.deleteUserById(id);    }    @PutMapping    public void adminEditUser(@RequestBody User user) {        service.editUser(user);    }}